mapper.update:
update test_user
name = #{name,jdbcType=VARCHAR}, age = #{age,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER}, birthday = #{birthday,jdbcType=DATE}, remarks = #{remarks,jdbcType=LONGVARCHAR}, mun = #{mun,jdbcType=DECIMAL}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP},
where id=#{id}

mapper.select:
		select
		id,
		name,
		age,
		status,
		birthday,
		remarks,
		mun,
		create_time,
		update_time
		from test_user

		 and test_user.id = #{id,jdbcType=INTEGER}
		 and test_user.name = #{name,jdbcType=VARCHAR}
		 and test_user.age = #{age,jdbcType=INTEGER}
		 and test_user.status = #{status,jdbcType=INTEGER}
		 and test_user.birthday = #{birthday,jdbcType=DATE}
		 and test_user.remarks = #{remarks,jdbcType=LONGVARCHAR}
		 and test_user.mun = #{mun,jdbcType=DECIMAL}
		 and test_user.create_time = #{createTime,jdbcType=TIMESTAMP}
		 and test_user.create_time BETWEEN #{startDate} and #{endDate}
		 and test_user.update_time = #{updateTime,jdbcType=TIMESTAMP}



              ${orderByString}


              order by id desc

